import fetch from 'isomorphic-unfetch';
import Navigation from '../components/Navigation';
import Layout from '../components/Layout';
import Head from 'next/head';

function About (props) {
console.warn("data", props)
return <Layout>
            <Head>
                <title>Rick and Morty - Personajes</title>
            </Head>
            <img id="logo" src="/img/rickMorty.png" alt="img rick aand morty"></img>
            <h1>
                Pesonajes importados desde Next y React
            </h1>

{
props.data.results.map(data=>(
    <div id="personajes">
        <div id="character">
            <h3>{data.name}</h3>
            <img src={data.image} alt='Personajes de serie img'></img>
            <p>{data.origin.name}</p>
            <p>{data.species}</p>
            <p>{data.gender}</p>
        </div>
    </div>
    ))
}

<style jsx>{`
html, body{
            margin: 0;
            padding: 0;
            border: 0;
        }
    #logo{
        display: block;
        width: 50%;
        margin: auto;
        padding: 2em;
    }
    h1{
        font-size: 18px;
        text-align: center;
        color: #F2F2F2;
    }

    #personajes{
        display: flex;
        flex-flow: row wrap;
    }
    #character{
        display: inline-block;
            width: 20%;
            height: 200px;
            background-color: aquamarine;
            border: 1px solid gray;
            border-radius: 5%;
            order: 0;

