import React from 'react';
import Navigation from './Navigation';

const Layout = ({children})=>{
    return(
        <>
            <div id="cabezal">
                <Navigation />                
            </div>
            <div id="contenido">
                {children}
            </div>
            <style jsx>{`
            #cabezal{
                background: #BF2642;
                width: 100%;
                height: 6.5em;
            }
            #contenido{
                background: #2F3540;
            }


            @media screen and (max-width: 1024px) {
                #contenido{
                    background: blue;
                }
            }
            @media screen and (max-width: 768px) {
                #contenido{
                    background: yellow;
                }                
            }
            @media screen and (max-width: 600px) {
                #contenido{
                    background: pink;
                }                
            }
        `}</style>
        </>
    )
}
export default Layout;
