import React from 'react';
import Layout from '../components/Layout';
import Head from 'next/head';

function About (props) {
console.warn("data", props)
return <Layout>
            <Head>
                <title>Rick and Morty - Personajes</title>
            </Head>
            <img id="logo" src="/img/rickMorty.png" alt="img rick aand morty"></img>
            <h1>
                Pesonajes importados desde Next y React
            </h1>

{
props.data.results.map(data=>(
    <div id="contenedor">
        <div id="item">
            <h3 id="nombre">{data.name}</h3>
            <img id="imgPer"src={data.image} alt='Personajes de serie img'></img>
            <div id="infoPer">
                <p>{data.origin.name}</p>
                <p>{data.species}</p>
                <p>{data.gender}</p>
            </div>            
        </div>                  
    </div>
    ))
}

<style jsx>{`
    #logo{
        display: block;
        width: 50%;
        margin: auto;
        padding: 2em;
    }
    h1{
        font-size: 18px;
        text-align: center;
        color: #F2F2F2;
    }
    #contenedor{
        display: initial;
        flex-wrap: wrap;
        justify-content: space-between;
        padding: 1em;
    }
    #item{
        display: inline-block;
        width: 30%;
        height: auto;
        background-color: #BF2642;
        border-radius: 2em;
        border: 1px solid #fff;
    }
    h3{
        font-size: 24px;
        text-align:center;
        color: #F2F2F2;
    }
    #imgPer{
        display: block;
        width: 90%;
        margin: auto;
    }
    #infoPer{
        font-size: 21px;
        text-align:center;
        color: #F2F2F2;
    }
                

