import React from 'react';
import fetch from 'isomorphic-unfetch';
import Navigation from '../components/Navigation';
import Layout from '../components/Layout';
import Head from 'next/head';

function About (props) {
console.warn("data", props)
return <Layout>
            <Head>
                <title>Rick and Morty - Personajes</title>
            </Head>
            <img id="logo" src="/img/rickMorty.png" alt="img rick aand morty"></img>
            <h1>
                Pesonajes importados desde Next y React
            </h1>
            <div class="contenedor">
        <div class="item">item1</div>        
        <div class="item">item2</div>        
        <div class="item">item3</div>        
        <div class="item">item4</div>        
        <div class="item">item5</div>        
        <div class="item">item6</div>        
        <div class="item item7">item7</div>        
        <div class="item">item8</div>        
        <div class="item">item9</div>
    </div>

{
props.data.results.map(data=>(
    <div id="personajes">
        <div id="character">
            <h3>{data.name}</h3>
            <img src={data.image} alt='Personajes de serie img'></img>
            <p>{data.origin.name}</p>
            <p>{data.species}</p>
            <p>{data.gender}</p>
        </div>
    </div>
    ))
}

<style jsx>{`
html, body{
    margin: 0;
    padding: 0;
    border: 0;
}
    #logo{
        display: block;
        width: 50%;
        margin: auto;
        padding: 2em;
    }
    h1{
        font-size: 18px;
        text-align: center;
        color: #F2F2F2;
    }

    #personajes{
        display: flex;
        flex-flow: row wrap;
    }
    #character{
        display: inline-block;
            width: 20%;
            height: 200px;
            background-color: aquamarine;
            border: 1px solid gray;
            border-radius: 5%;
            order: 0;

    }

    .contenedor{
        display: flex;
        /*flex-direction: column; direccion de los items
        flex-wrap: wrap;*/             
        flex-flow: row wrap; /*ahorra lineas de código*/
        /*justify-content: flex-start;  space-between-etc ajusa los intems con espacios entre ellos de forma horizontal
        align-items acomoda de forma vertical
        align content acomoda todo el bloque*/
        
    }
    .item{
        display: inline-block;
        width: 20%;
        height: 200px;
        background-color: aquamarine;
        border: 1px solid gray;
        border-radius: 5%;
        order: 0;
    }
                

